mapper.insert:

insert into ASSESSMENT_SUBSCRIBER (SUBSCRIBER_ID, ASSESSMENT_EVENT_ID, 
  ASSESSMENT_ID, ZONE_ID, AMOUNT, 
  TO_STAFF_ID, STATE_DATE, STATE
  )
values (#{subscriberId,jdbcType=DECIMAL}, #{assessmentEventId,jdbcType=DECIMAL}, 
  #{assessmentId,jdbcType=DECIMAL}, #{zoneId,jdbcType=DECIMAL}, #{amount,jdbcType=DECIMAL}, 
  #{toStaffId,jdbcType=DECIMAL}, #{stateDate,jdbcType=TIMESTAMP}, #{state,jdbcType=VARCHAR}
  )

mapper.update:
UPDATE ASSESSMENT_SUBSCRIBER
SET state = 'FED'
WHERE ASSESSMENT_ID=#{assessmentId}

UPDATE ASSESSMENT_SUBSCRIBER
SET state = 'AUD'
WHERE ASSESSMENT_ID=#{assessmentId}

UPDATE ASSESSMENT_SUBSCRIBER
  SET state = 'REP'
  WHERE ASSESSMENT_ID=#{assessmentId}

UPDATE ASSESSMENT_SUBSCRIBER
SET state=#{toState}
Where ASSESSMENT_ID = #{assessmentId} and state=#{fromState}
