mapper.delete:
UPDATE STAFF_ASSESSMENT_ITEM
set state='INU',STATE_DATE=sysdate
WHERE STAFF_ASSESSMENT_ID = #{staffAssessmentId}

mapper.insert:

insert into STAFF_ASSESSMENT_ITEM (ID, STAFF_ASSESSMENT_ID, ITEM_ID, 
  ROLE_CODE, ASSESSMENT_PERCENT, ITEM_NAME, 
  CRITERION_SCORE, CRITERION_DETAIL, STATE, 
  STATE_DATE, CATEGORY_ID, SCORE
  )
values (#{id,jdbcType=DECIMAL}, #{staffAssessmentId,jdbcType=DECIMAL}, #{itemId,jdbcType=DECIMAL}, 
  #{roleCode,jdbcType=VARCHAR}, #{assessmentPercent,jdbcType=DECIMAL}, #{itemName,jdbcType=VARCHAR}, 
  #{criterionScore,jdbcType=DECIMAL}, #{criterionDetail,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}, 
  #{stateDate,jdbcType=TIMESTAMP}, #{categoryId,jdbcType=DECIMAL}, #{score,jdbcType=DECIMAL}
  )
